mapper.select:
SELECT * FROM tp_manufacture.plants
WHERE plant_type = #{plantType}
ORDER BY plant_name

SELECT * FROM tp_manufacture.plants
WHERE status = #{status}
ORDER BY plant_name

mapper.insert:
INSERT INTO tp_manufacture.plants(
    plant_id, plant_name, plant_name_en, address, address_detail,
    postal_code, coordinates, plant_type, status, total_area,
    established_date, manager_emp_id, phone, fax
) VALUES (
    #{plantId}, #{plantName}, #{plantNameEn}, #{address}, #{addressDetail},
    #{postalCode}, #{coordinates}, #{plantType}, #{status}, #{totalArea},
    #{establishedDate}, #{managerEmpId}, #{phone}, #{fax}
)
